import useMounajiTestContract from "../../hooks/useMounajiTestContract";
import {
  Stack,
  Flex,
  Heading,
  Text,
  Box,
  Button,
  Image,
  Badge,
  useToast
} from "@chakra-ui/react";
import { Link } from "react-router-dom";
import { useWeb3React } from "@web3-react/core";
import { useCallback, useEffect, useState } from "react";

const Ecosystem = () => {
  const [isMinting, setIsMinting] = useState(false);
  const [imageSrc, setImageSrc] = useState("");
  const { active, account } = useWeb3React();
  const mounajiTestContract = useMounajiTestContract();
  const toast = useToast();
  


  
  return (
    
  <Box
 
   
  maxW='fixed' borderWidth='1px' borderRadius='lg'
  // backgroundImage="'/images/fondoespacio.png'"
  
  pos={"relative"}
  align={"center"}
  justify={"center"}
  backgroundRepeat={"no-repeat"}
  // backgroundSize={"cover"}
  margin={"0"}
  top={"auto" }
  left={"0"}
  w={"100%"}
  h={"100%"}
  padding={0}
  
>
<Stack
marginTop={'40px'}>
  <Text
  fontSize={'56px'}>
    Ecosystem

  </Text>
  </Stack>
{/* <Image 
  maxW='700px' borderWidth='1px' borderRadius='lg' overflow='scroll'
  marginTop={"-200px"}
  // src={"/images/starway.png"}
  
  
  
/> */}
  
<Stack
      
      align={"center"}
      spacing={{ base: 8, md: 10 }}
       py={{ base: -920, md: -920 }}
      direction={{ base: "column-reverse", md: "row" }}
    >
      <Stack flex={1} spacing={{ base: 5, md: 10 }}
      align={'center'}>
        <Heading
          lineHeight={1.1}
          fontWeight={600}
          alignSelf={'center'}
          backgroundColor={'lightgrey'}
          fontSize={{ base: "3xl", sm: "4xl", lg: "6xl" }}
        >
        Market flow system explanation 
        </Heading>
                
        <Text color={"gray.500"}
        fontSize={'48px'}
        align={'center'}
        >
          
        </Text>
        <Text color={"green.500"}>
          Get your Stars and start ean for playing now !
        </Text>
        <Stack
          spacing={{ base: 4, sm: 6 }}
          direction={{ base: "column", sm: "row" }}
        >
          <Button
            rounded={"full"}
            size={"lg"}
            fontWeight={"normal"}
            px={6}
            colorScheme={"green"}
            bg={"green.400"}
            _hover={{ bg: "green.500" }}
            disabled={!mounajiTestContract}
            
          >
            Get your Star Token !
          </Button>
          <Link to="/swaptokens">
            <Button rounded={"full"} size={"lg"} fontWeight={"normal"} px={6}>
              SwapTokens
            </Button>
          </Link>
        </Stack>
      </Stack>
      <Flex
        flex={1}
        direction="column"
        justify={"center"}
        align={"center"}
        position={"relative"}
        w={"full"}
      >
        <Image 
        src={active ? imageSrc : "../../images/starway.png"} />
        {active ? (
          <>
            
          </>
        ) : (
          <Badge mt={2}>Wallet desconectado</Badge>
        )}
      </Flex>
    </Stack>
    </Box>  
      
    
  );
};

export default Ecosystem;
